import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            obenOpen: false,
            enter: false,
            großValue: ''
        }
}

obenclicked(){
        this.setState({obenOpen: true})
}

search(e){
        e.key === 'Enter' ? console.log("enter pressed") : console.log("dont know")
}

handlechange(e){
        this.setState({großValue: e.value})
}

render() {
    return (
        <div className="App">
            <div className="main-background">
                <h1 className="headline">One Big Thing</h1>
                <div className={this.state.obenOpen ? "btn-oben-open" : "btn-oben"} onClick={e => this.obenclicked()}>
                    {this.state.enter ?
                        <div className="plus"></div>
                   : <div className="plus">{this.state.obenOpen ? <div><input onChange={e => this.handlechange(e)} value= {this.state.großValue} className="input" onKeyDown={ e => this.search(e)}/></div> : <div className="plus">+</div>}</div>
                        }
                </div>
                <div className="little-things-wrapper">
                    <div className="btn">
                        <div className="plus">+</div>
                    </div>
                    <div className="btn">
                        <div className="plus">+</div>
                    </div>
                    <div className="btn">
                        <div className="plus">+</div>
                    </div>
                </div>
                <div className="weiter">
                    <div className="weiter-text"> ></div>
                </div>
                <div className="zurück">
                    <div className="zurück-text"> ></div>
                </div>
            </div>
        </div>
    );
  }
}

export default App;
